import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-index-header',
  templateUrl: './index-header.component.html',
  styleUrls: ['./index-header.component.css']
})
export class IndexHeaderComponent{
  isCollapsed: boolean = false;

  constructor(private router: Router){

  }

  startAgain(){
    localStorage.clear();
    this.router.navigate([""]);
  }
}
<nav class="navbar navbar-expand-md">
    <div class="container-fluid">
        <!-- Logo on the left -->
        <a class="navbar-brand" href="#">
            <img src="assets/pictures/Art-digital-logo.png" alt="ArtDigital Logo" width="100">
        </a>
        <!-- Toggler Button for all screens, at the end (right) -->
        <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavDropdown" aria-controls="navbarNavDropdown" aria-expanded="false" aria-label="Toggle navigation" (click)="isCollapsed = !isCollapsed">
            <span class="navbar-toggler-icon"></span>
        </button>

        <div class="mx-auto d-none d-md-block">
            <h2 class="text-white text-center">Izrada fotografija online</h2>
        </div>
        <div *ngIf="isCollapsed">
            <a class="nav-link active text-white" (click)="startAgain()">Početna <i class="bi bi-house"></i></a>
        </div>


    </div>
</nav>
