import { Component, ElementRef, ViewChild } from '@angular/core';
import { Router } from '@angular/router';

declare var bootstrap: any; // This is for Bootstrap's JavaScript

@Component({
  selector: 'app-index-header',
  templateUrl: './index-header.component.html',
  styleUrls: ['./index-header.component.css']
})
export class IndexHeaderComponent{
  @ViewChild('priceModal') priceModalRef!: ElementRef;

  constructor(private router: Router){

  }

  startAgain(){
    localStorage.clear();
    this.router.navigate([""]);
  }

  openPriceModal() {
    // Dismiss the offcanvas first
    const offcanvasElement = document.getElementById('offcanvasNavbar');
    const bsOffcanvas = new bootstrap.Offcanvas(offcanvasElement);

    // This event is fired when the offcanvas has finished being hidden from the user
    if(offcanvasElement == null)return;
    offcanvasElement.addEventListener('hidden.bs.offcanvas', () => {
      this.showPriceModal();
    }, { once: true }); // The { once: true } option auto-removes the event listener after it fires once

    bsOffcanvas.hide();
  }

  showPriceModal() {
    // Now show the modal for prices
    const modalNative: HTMLElement = this.priceModalRef.nativeElement;
    const modal = new bootstrap.Modal(modalNative, {
      backdrop: 'static',
      keyboard: false,
    });
    modal.show();
  }

}
