  templateUrl: './view-order.component.html',
  styleUrls: ['./view-order.component.css']
})
export class ViewOrderComponent implements OnInit{
  name: string = "Polje trenutno nije popunjeno.";
  surname: string = "Polje trenutno nije popunjeno.";
  email: string = "Polje trenutno nije popunjeno.";
  phone: string = "Polje trenutno nije popunjeno.";
  orderType: string = "Trenutno nije izabran način preuzimanja.";
  place: string = "Trenutno nije izabran način preuzimanja.";
  postal: string = "Trenutno nije izabran način preuzimanja.";
  city: string = "Trenutno nije izabran način preuzimanja.";
  address: string = "Trenutno nije izabran način preuzimanja.";
  numberOfPictures: number = 0;
  numberOfExtras: number = 0;
  totalPrice: number = 0;
  parentComponent: string = "";

  constructor(){}
  ngOnInit(): void {
    let localparent = localStorage.getItem("parentComponent");
