    let localordertype = localStorage.getItem("pickup");
    let localplace = localStorage.getItem("location");
    let localpostal = localStorage.getItem("postal");
    let localcity = localStorage.getItem("city");
    let localaddress = localStorage.getItem("address");
    let localnumofpictures = localStorage.getItem("imageBlobsLength");
    let localnumofextras = localStorage.getItem("extrasLength");
