import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-view-order',
  templateUrl: './view-order.component.html',
  styleUrls: ['./view-order.component.css']
})
export class ViewOrderComponent implements OnInit{
  name: string = "";
  surname: string = "";
  email: string = "";
  phone: string = "";
  orderType: string = "";
  place: string = "";
  postal: string = "";
  city: string = "";
  address: string = "";
  numberOfPictures: number = 0;
  numberOfExtras: number = 0;
  totalPrice: number = 0;

  constructor(){}
  ngOnInit(): void {
    let localname = localStorage.getItem("name");
    let localsurname = localStorage.getItem("surname");
    let localemail = localStorage.getItem("email");
    let localphone = localStorage.getItem("phone");
    let localordertype = localStorage.getItem("pickup");
    let localplace = localStorage.getItem("location");
    let localpostal = localStorage.getItem("postal");
    let localcity = localStorage.getItem("city");
    let localaddress = localStorage.getItem("address");
    let localnumofpictures = localStorage.getItem("imageBlobsLength");
    let localnumofextras = localStorage.getItem("");
  }


}
<p>view-order works!</p>
Ime: {{name}}
Prezime: {{surname}}
Email: {{email}}
Telefon: {{phone}}
<div *ngIf="orderType=='licno'">
    Preuzimanje u poslovnici: {{place}}
</div>
<div *ngIf="orderType=='kurir'">
    Poštanski broj: {{postal}}
    Grad: {{city}}
    Adresa: {{address}}
</div>
Izabrali ste: {{numberOfPictures}} slika.
Izabrali ste: {{numberOfExtras}} seta dodataka.
Cena porudžbine je: {{totalPrice}}
